mapper.update:
UPDATE PAYMENT SET
  payment_status = 1,
  imp_uid = #{impUid},
  pg_tid = #{pgTid},
  receipt_url = #{receiptUrl}
WHERE merchant_uid = #{merchantUid}

UPDATE PAYMENT
SET payment_status = -1,
    fail_reason    = #{failReason}
WHERE merchant_uid = #{merchantUid}

UPDATE PAYMENT
SET payment_status = #{status},
    fail_reason    = #{reason},
    receipt_url    = #{receiptUrl}
WHERE merchant_uid = #{merchantUid}

UPDATE PAYMENT
SET order_id = #{orderId}
WHERE merchant_uid = #{merchantUid}
